namespace KSA\GeneralApi\Test\Integration\Api\Thumbnail;

use KSA\GeneralApi\Api\Thumbnail\Get;
use KSA\GeneralApi\Test\TestCase;
use KSP\Api\IResponse;
use KSP\Core\DTO\File\IExtension;

class GetTest extends TestCase {

    public function testWithMissingExtension(): void {
        /** @var Get $get */
        $get      = $this->getService(Get::class);
        $response = $get->handle(
            $this->getDefaultRequest()
        );
        $this->assertTrue(false === $this->getResponseService()->isValidResponse($response));
        $this->assertTrue(IResponse::BAD_REQUEST === $response->getStatusCode());
    }

    public function testWithUnsupportedExtensionButGettingDefaultExtension(): void {
        /** @var Get $get */
        $get      = $this->getService(Get::class);
        $response = $get->handle(
            $this->getDefaultRequest()
                ->withAttribute('extension', 'nizanim')
        );
        $this->assertTrue(true === $this->getResponseService()->isValidResponse($response));
        $this->assertTrue(
            ((string) $response->getBody()) === ''
        );
    }

    /**
     * @param string $name
     * @param string $expected
     * @return void
     * @dataProvider provideValidExtensions
     */
    public function testValidExtensions(string $name, string $expected): void {
        /** @var Get $get */
        $get      = $this->getService(Get::class);
        $response = $get->handle(
            $this->getDefaultRequest()
                ->withAttribute('extension', $name)
        );
        $this->assertTrue(true === $this->getResponseService()->isValidResponse($response));
        $this->assertTrue(
            ((string) $response->getBody()) === $expected
        );
    }

    public function provideValidExtensions(): array {
        return [
            [IExtension::JPEG, '']
            , [IExtension::JPG, '']
            , [IExtension::PNG, '']
            , [IExtension::PDF, '']
            , [IExtension::DOCX, '']
            , [IExtension::DOC, '']
            , [IExtension::TEXT, '']
        ];
    }

}